import{c as g}from"./chunk-L46JRZOO.js";import{Fa as l,Ga as h,J as t,Oa as o,Qa as d,ia as a,oa as s,ya as u}from"./chunk-KBGHTQJU.js";var c=class r{text="Jetzt beraten lassen";link="/kontakt";static \u0275fac=function(n){return new(n||r)};static \u0275cmp=s({type:r,selectors:[["app-cta-button"]],inputs:{text:"text",link:"link"},decls:2,vars:2,consts:[[1,"cta-button",3,"routerLink"]],template:function(n,i){n&1&&(l(0,"a",0),o(1),h()),n&2&&(u("routerLink",i.link),a(),d(" ",i.text," "))},dependencies:[g],styles:['.cta-button[_ngcontent-%COMP%]{display:inline-block;padding:1rem 2rem;background-color:var(--primary-yellow);color:var(--text-dark);text-decoration:none;border-radius:var(--border-radius);font-weight:700;font-size:1.05rem;text-align:center;transition:all var(--transition-base);box-shadow:var(--shadow-sm);border:3px solid var(--primary-yellow);position:relative;overflow:hidden}.cta-button[_ngcontent-%COMP%]:before{content:"";position:absolute;top:50%;left:50%;width:0;height:0;border-radius:50%;background:#ffffff4d;transform:translate(-50%,-50%);transition:width var(--transition-base),height var(--transition-base)}.cta-button[_ngcontent-%COMP%]:hover{background-color:var(--primary-yellow-dark);transform:translateY(-3px);box-shadow:var(--shadow-lg)}.cta-button[_ngcontent-%COMP%]:hover:before{width:400px;height:400px}.cta-button[_ngcontent-%COMP%]:active{transform:translateY(-1px)}']})};var f=class r{services=[{id:"dachsanierung",title:"Dachsanierung Nahe Glan \u2013 Professionell und nachhaltig",headline:"Dachsanierung vom Meisterbetrieb",description:"Ihr Dach ist mehr als nur Schutz \u2013 es ist ein zentraler Bestandteil Ihres Hauses. Mit einer professionellen Dachsanierung verl\xE4ngern Sie die Lebensdauer Ihres Daches, steigern die Energieeffizienz und erh\xF6hen den Wert Ihrer Immobilie.",benefits:["Langfristiger Schutz vor Witterung und Sch\xE4den","Energieeffizienz durch moderne D\xE4mmsysteme","Wertsteigerung Ihrer Immobilie","Individuelle L\xF6sungen f\xFCr Steild\xE4cher und Flachd\xE4cher","Fachgerechte Ausf\xFChrung nach aktuellen Standards"],process:["Kostenlose Erstberatung und Besichtigung vor Ort","Detaillierte Schadensanalyse und Zustandsbewertung","Transparentes Angebot mit allen Kosten","Professionelle Ausf\xFChrung durch erfahrene Dachdecker","Saubere Baustelle und p\xFCnktliche Fertigstellung","Abnahme und Dokumentation der durchgef\xFChrten Arbeiten"],faqs:[{question:"Wann ist eine Dachsanierung notwendig?",answer:"Eine Dachsanierung wird empfohlen bei sichtbaren Sch\xE4den (Risse, fehlende Ziegel), bei Undichtigkeiten, starkem Moosbewuchs oder wenn Ihr Dach \xE4lter als 30-40 Jahre ist. Auch bei unzureichender D\xE4mmung ist eine energetische Sanierung sinnvoll."},{question:"Wie lange dauert eine Dachsanierung?",answer:"Die Dauer h\xE4ngt von der Dachgr\xF6\xDFe und dem Sanierungsumfang ab. Ein Einfamilienhaus (ca. 150m\xB2) dauert in der Regel 1-3 Wochen. Wir erstellen Ihnen einen detaillierten Zeitplan."},{question:"Was kostet eine Dachsanierung?",answer:"Die Kosten variieren je nach Dachfl\xE4che, Material und Aufwand. F\xFCr ein Einfamilienhaus k\xF6nnen Sie mit 100-250\u20AC pro m\xB2 rechnen. Kontaktieren Sie uns f\xFCr ein kostenloses, unverbindliches Angebot."},{question:"Welche F\xF6rderm\xF6glichkeiten gibt es?",answer:"F\xFCr energetische Dachsanierungen gibt es F\xF6rderungen \xFCber die KfW und BAFA. Wir beraten Sie gerne zu den aktuellen F\xF6rderprogrammen und unterst\xFCtzen Sie bei der Antragstellung."}],keywords:"Dachsanierung Nahe Glan, Dach sanieren, Dacherneuerung, Steildach, Flachdach, energetische Sanierung, Bad Kreuznach, Kirn",icon:"\u{1F3E0}"},{id:"dachfenster",title:"Dachfenster Nahe Glan \u2013 Mehr Licht und Wohnkomfort",headline:"Dachfenster-Einbau vom Fachbetrieb",description:"Dachfenster bringen nat\xFCrliches Licht in Ihr Dachgeschoss und schaffen ein angenehmes Wohnklima. Wir sind Ihr Fachbetrieb f\xFCr Einbau, Austausch und Reparatur von Dachfenstern in Nahe Glan und Umgebung.",benefits:["Bis zu 3x mehr Tageslicht als Fassadenfenster","Bessere Bel\xFCftung und gesundes Raumklima","Energieeffiziente 3-fach-Verglasung","Wertsteigerung Ihrer Immobilie","Vielf\xE4ltige Designs und Gr\xF6\xDFen verf\xFCgbar"],process:["Beratung zu Gr\xF6\xDFe, Position und Fenstertyp","Auswahl des passenden Dachfensters (z.B. VELUX)","Pr\xE4ziser Einbau durch erfahrene Dachdecker","Fachgerechte Abdichtung und Integration","Funktionspr\xFCfung und Einweisung","Optional: Installation von Rolll\xE4den oder Sonnenschutz"],faqs:[{question:"Welche Dachfenster-Typen gibt es?",answer:"Die g\xE4ngigsten Typen sind Schwingfenster (klassisch), Klapp-Schwingfenster (mit Balkon-Funktion) und Ausstiegsfenster f\xFCr den Zugang aufs Dach. Wir beraten Sie, welcher Typ f\xFCr Ihre Bed\xFCrfnisse ideal ist."},{question:"Kann jedes Dach ein Dachfenster bekommen?",answer:"Grunds\xE4tzlich ja, allerdings m\xFCssen die Dachneigung (15-90\xB0) und die Dachkonstruktion gepr\xFCft werden. Bei Flachd\xE4chern gibt es spezielle L\xF6sungen wie Lichtkuppeln."},{question:"Wie lange dauert der Einbau?",answer:"Der Einbau eines Dachfensters dauert in der Regel 4-8 Stunden pro Fenster, abh\xE4ngig von der Dachkonstruktion und dem gew\xE4hlten Fenstertyp."},{question:"Arbeiten Sie mit Markenherstellern?",answer:"Ja, wir sind Partner von f\xFChrenden Herstellern wie VELUX, Roto und Fakro und bieten Ihnen hochwertige Produkte mit Herstellergarantie."}],keywords:"Dachfenster Nahe Glan, Dachfenster einbauen, VELUX, Roto, Dachfl\xE4chenfenster, Bad Kreuznach, Kirn",icon:"\u{1FA9F}"},{id:"dachreparaturen",title:"Dachreparaturen und Wartung Nahe Glan \u2013 Schnell und zuverl\xE4ssig",headline:"Notdienst und regelm\xE4\xDFige Dachwartung",description:"Ein Dach braucht Pflege. Von der Notfall-Reparatur nach einem Sturm bis zur regelm\xE4\xDFigen Inspektion \u2013 wir k\xFCmmern uns um Ihr Dach und halten es in Top-Zustand.",benefits:["Schneller Notdienst bei Sturmsch\xE4den","Vermeidung kostspieliger Folgesch\xE4den","Regelm\xE4\xDFige Wartung verl\xE4ngert die Lebensdauer","Transparente Preise ohne versteckte Kosten","Dokumentation aller durchgef\xFChrten Arbeiten"],process:["Schadensmeldung per Telefon, E-Mail oder Kontaktformular","Bei Notf\xE4llen: Schnelle Terminvereinbarung (meist innerhalb 24h)","Vor-Ort-Begutachtung und Schadensanalyse","Sofortreparatur bei kleinen Sch\xE4den m\xF6glich","Bei gr\xF6\xDFerem Aufwand: Detailliertes Angebot","Professionelle Reparatur und Dokumentation"],faqs:[{question:"Wie schnell k\xF6nnen Sie bei einem Notfall vor Ort sein?",answer:"Bei Notf\xE4llen wie Sturmsch\xE4den oder undichten Stellen bem\xFChen wir uns, innerhalb von 24 Stunden bei Ihnen zu sein. Kontaktieren Sie uns telefonisch f\xFCr eine schnelle Reaktion."},{question:"Wie oft sollte ein Dach gewartet werden?",answer:"Wir empfehlen eine professionelle Inspektion alle 2-3 Jahre. Bei \xE4lteren D\xE4chern (20+ Jahre) oder nach starken St\xFCrmen sollte j\xE4hrlich kontrolliert werden."},{question:"Was beinhaltet eine Dachwartung?",answer:"Bei der Wartung pr\xFCfen wir die Dacheindeckung, Abdichtungen, Regenrinnen, Dachfenster und alle kritischen Bereiche. Kleine M\xE4ngel beheben wir direkt, gr\xF6\xDFere Sch\xE4den dokumentieren wir im Wartungsprotokoll."},{question:"Bieten Sie Wartungsvertr\xE4ge an?",answer:"Ja, besonders f\xFCr Hausverwaltungen, Gewerbeimmobilien und Mehrfamilienh\xE4user bieten wir individuelle Wartungsvertr\xE4ge mit festen Terminen an."}],keywords:"Dachreparatur Nahe Glan, Dach reparieren, Notdienst, Sturmschaden, Dachwartung, Dachinspektion, Bad Kreuznach",icon:"\u{1F527}"},{id:"regenrinnen",title:"Regenrinnen Nahe Glan \u2013 Installation, Reparatur und Reinigung",headline:"Professionelle Regenrinnen-Services",description:"Regenrinnen sind essentiell f\xFCr den Schutz Ihres Hauses vor Feuchtigkeit und Wassersch\xE4den. Wir installieren, reparieren und warten Regenrinnen sowie Fallrohre im Raum Nahe Glan.",benefits:["Schutz vor Feuchtigkeitssch\xE4den am Mauerwerk","Professionelle Rinnenreinigung","Austausch alter oder besch\xE4digter Rinnen","Installation von Laubschutzgittern","Materialauswahl: Kupfer, Zink, Aluminium oder Kunststoff"],process:["Inspektion der vorhandenen Regenrinnen","Reinigung und Begutachtung des Zustands","Beratung zu Materialien und L\xF6sungen","Reparatur oder kompletter Austausch bei Bedarf","Fachgerechte Montage neuer Rinnen","Optional: Installation von Laubschutz-Gittern"],faqs:[{question:"Wie oft sollten Regenrinnen gereinigt werden?",answer:"Mindestens zweimal j\xE4hrlich \u2013 idealerweise im Fr\xFChjahr und Herbst. Bei vielen B\xE4umen in der N\xE4he empfehlen wir h\xE4ufigere Kontrollen."},{question:"Was kostet eine Rinnenreinigung?",answer:"Die Kosten h\xE4ngen von der L\xE4nge und Zug\xE4nglichkeit der Rinnen ab. In der Regel liegen die Kosten bei 2-5\u20AC pro laufendem Meter. Kontaktieren Sie uns f\xFCr ein genaues Angebot."},{question:"Welches Material ist am besten f\xFCr Regenrinnen?",answer:"Kupfer ist sehr langlebig (50+ Jahre), aber teurer. Zink ist robust und preiswert. Aluminium ist leicht und korrosionsbest\xE4ndig. Kunststoff ist kosteng\xFCnstig, aber weniger langlebig. Wir beraten Sie gerne."},{question:"Lohnt sich ein Laubschutz?",answer:"Ja, besonders bei vielen B\xE4umen in der N\xE4he. Ein Laubschutzgitter reduziert den Reinigungsaufwand erheblich und verhindert Verstopfungen."}],keywords:"Regenrinnen Nahe Glan, Rinnenreinigung, Fallrohre, Laubschutz, Dachrinnen, Bad Kreuznach, Kirn",icon:"\u{1F4A7}"},{id:"flachdachpruefung",title:"Flachdachdichtheitspr\xFCfung Nahe Glan \u2013 Nach DIN-Norm",headline:"Professionelle Flachdach-Dichtheitspr\xFCfung",description:"F\xFCr Gewerbeimmobilien, Hallen und Garagen: Wir pr\xFCfen Ihr Flachdach auf Dichtheit mit modernen Verfahren nach DIN-Norm und erstellen detaillierte Pr\xFCfprotokolle.",benefits:["Fr\xFCherkennung von Leckagen vor gro\xDFen Sch\xE4den","Pr\xFCfung nach DIN 18531 und DIN 18195","Moderne Pr\xFCfverfahren (Rauchgas, Dampf, Rauchimpuls)","Detaillierte Dokumentation mit Fotos und Protokoll","Angebot zur Behebung festgestellter M\xE4ngel"],process:["Terminvereinbarung und Vorbesprechung","Auswahl des geeigneten Pr\xFCfverfahrens","Durchf\xFChrung der Dichtheitspr\xFCfung vor Ort","Pr\xE4zise Lokalisierung undichter Stellen","Fotodokumentation aller Befunde","Erstellung eines detaillierten Pr\xFCfprotokolls","Optional: Angebot zur M\xE4ngelbehebung"],faqs:[{question:"Welche Pr\xFCfverfahren gibt es?",answer:"Wir nutzen Rauchgas-Verfahren, Dampferzeugungs-Verfahren und Rauchimpuls-Verfahren. Die Wahl h\xE4ngt vom Dachaufbau, der Gr\xF6\xDFe und den baulichen Gegebenheiten ab."},{question:"Wie lange dauert eine Flachdachpr\xFCfung?",answer:"Je nach Dachgr\xF6\xDFe zwischen 2-6 Stunden. Bei sehr gro\xDFen Hallen kann die Pr\xFCfung auch einen ganzen Tag dauern."},{question:"F\xFCr wen ist die Pr\xFCfung relevant?",answer:"Ideal f\xFCr Gewerbegeb\xE4ude, Lagerhallen, Industriebauten, Tiefgaragen, Balkone und alle Flachdach-Konstruktionen. Besonders wichtig vor Ablauf der Gew\xE4hrleistung oder bei Verdacht auf Undichtigkeiten."},{question:"Wird die Pr\xFCfung dokumentiert?",answer:"Ja, Sie erhalten ein ausf\xFChrliches Pr\xFCfprotokoll mit Fotos, Markierungen der undichten Stellen und Handlungsempfehlungen."}],keywords:"Flachdachdichtheitspr\xFCfung Nahe Glan, Flachdach pr\xFCfen, DIN-Pr\xFCfung, Gewerbe, Hallen, Bad Kreuznach",icon:"\u{1F50D}"},{id:"pv-anlagen",title:"PV-Anlagen Nahe Glan \u2013 Solarenergie f\xFCr Ihr Dach",headline:"Photovoltaik-Anlagen vom Fachbetrieb",description:"Nutzen Sie die Kraft der Sonne! Wir planen und installieren moderne Photovoltaik-Anlagen auf Ihrem Dach \u2013 f\xFCr nachhaltige Energiegewinnung, Unabh\xE4ngigkeit und Kosteneinsparung.",benefits:["Bis zu 70% Reduzierung der Stromkosten","Unabh\xE4ngigkeit von steigenden Energiepreisen","CO\u2082-neutrale Stromerzeugung","Wertsteigerung Ihrer Immobilie","Attraktive F\xF6rderm\xF6glichkeiten und KfW-Zusch\xFCsse","Professionelle Planung und Installation"],process:["Kostenlose Erstberatung und Standortanalyse","Berechnung des Energiebedarfs und der Anlagengr\xF6\xDFe","Beratung zu F\xF6rderm\xF6glichkeiten (KfW, BAFA)","Detailliertes Angebot mit Wirtschaftlichkeitsrechnung","Fachgerechte Installation der PV-Module","Elektrischer Anschluss und Inbetriebnahme","Anmeldung beim Netzbetreiber und Einweisung"],faqs:[{question:"Lohnt sich eine PV-Anlage in unserer Region?",answer:"Ja! Auch in Rheinland-Pfalz ist die Sonneneinstrahlung ausreichend f\xFCr rentable PV-Anlagen. Mit ca. 1.000-1.200 kWh pro kWp Anlagenleistung k\xF6nnen Sie rechnen."},{question:"Welche F\xF6rderm\xF6glichkeiten gibt es?",answer:"Es gibt verschiedene KfW-Programme (z.B. KfW 270 f\xFCr zinsg\xFCnstige Kredite), BAFA-Zusch\xFCsse und regionale F\xF6rderungen. Au\xDFerdem profitieren Sie von der Einspeiseverg\xFCtung nach EEG. Wir beraten Sie umfassend."},{question:"Wie gro\xDF sollte meine PV-Anlage sein?",answer:"Das h\xE4ngt von Ihrem Stromverbrauch und der verf\xFCgbaren Dachfl\xE4che ab. F\xFCr einen 4-Personen-Haushalt empfehlen wir ca. 5-8 kWp (ca. 30-50m\xB2 Modulfl\xE4che). Wir erstellen eine individuelle Bedarfsanalyse."},{question:"Wie lange dauert die Installation?",answer:"Die Installation dauert je nach Anlagengr\xF6\xDFe 2-5 Tage. Von der Planung bis zur Inbetriebnahme sollten Sie 4-8 Wochen einplanen (inkl. Genehmigungen und Netzbetreiber-Anmeldung)."},{question:"Brauche ich einen Stromspeicher?",answer:"Ein Stromspeicher erh\xF6ht Ihre Eigenverbrauchsquote von ca. 30% auf 60-80% und macht Sie unabh\xE4ngiger. Die Anschaffung lohnt sich, wenn Sie m\xF6glichst viel selbst erzeugten Strom nutzen m\xF6chten."}],keywords:"PV-Anlagen Nahe Glan, Photovoltaik, Solaranlage, Sonnenenergie, Stromspeicher, KfW-F\xF6rderung, Bad Kreuznach, Kirn",icon:"\u2600\uFE0F"},{id:"hallenbeleuchtung",title:"Hallen- und Fassadenbeleuchtung Nahe Glan",headline:"Moderne LED-Beleuchtung f\xFCr Gewerbe",description:"Energieeffiziente LED-Beleuchtung f\xFCr Industrie- und Gewerbehallen sowie repr\xE4sentative Fassadenbeleuchtung \u2013 professionell geplant und installiert von unserem Fachteam.",benefits:["Bis zu 80% Energieeinsparung durch LED-Technik","Bessere Ausleuchtung f\xFCr optimale Arbeitsbedingungen","Langlebige Technik mit bis zu 50.000 Betriebsstunden","Geringerer Wartungsaufwand als herk\xF6mmliche Systeme","Repr\xE4sentative Fassadenbeleuchtung f\xFCr Ihr Unternehmen","Professionelle Lichtplanung nach Ihren Anforderungen"],process:["Bedarfsanalyse und Lichtplanung vor Ort","Berechnung der optimalen Ausleuchtung (Lux-Werte)","Auswahl passender LED-Systeme","Angebot mit Wirtschaftlichkeitsberechnung","Professionelle Installation durch Fachpersonal","Elektrischer Anschluss und Inbetriebnahme","Einweisung und Dokumentation"],faqs:[{question:"F\xFCr welche Objekte ist das geeignet?",answer:"Ideal f\xFCr Lagerhallen, Produktionshallen, Werkst\xE4tten, Logistikzentren, Gewerbefassaden und alle Industriebauten, die eine professionelle Beleuchtung ben\xF6tigen."},{question:"Wie hoch ist die Energieeinsparung?",answer:"LED-Beleuchtung spart gegen\xFCber herk\xF6mmlichen Halogen- oder Leuchtstoffr\xF6hren bis zu 80% Energie. Bei einer 1.000m\xB2 Halle k\xF6nnen Sie mit mehreren tausend Euro Einsparung pro Jahr rechnen."},{question:"Wie lange halten LED-Leuchtmittel?",answer:"Hochwertige LED-Hallenstrahler haben eine Lebensdauer von 50.000-100.000 Betriebsstunden. Das entspricht bei 10h Betrieb/Tag ca. 15-25 Jahren."},{question:"Bieten Sie auch Wartung an?",answer:"Ja, wir bieten Wartungsvertr\xE4ge f\xFCr Gewerbekunden an, die regelm\xE4\xDFige Inspektionen und schnelle Reparaturen bei Ausf\xE4llen umfassen."}],keywords:"Hallenbeleuchtung Nahe Glan, LED Beleuchtung, Fassadenbeleuchtung, Gewerbe, Industriehallen, Bad Kreuznach",icon:"\u{1F4A1}"}];getAllServices(){return this.services}getServiceById(e){return this.services.find(n=>n.id===e)}getServicesOverview(){return this.services.map(e=>({id:e.id,title:e.headline,description:e.description,icon:e.icon}))}static \u0275fac=function(n){return new(n||r)};static \u0275prov=t({token:r,factory:r.\u0275fac,providedIn:"root"})};export{c as a,f as b};
